import "./Expenses.css"
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpenseFilter";
import { useState } from "react";

function Expenses(props) {
    const [filteredYear, setFilteredYear] = useState("2021");

    const filterChangeHandler = (selectedYear) => {
        console.log("Expenses.js filtered " + selectedYear);
        setFilteredYear(selectedYear);
    };

    const filteredExpenses = props.items.filter(eachExpense => (eachExpense.date.getFullYear().toString() === filteredYear)
    );
    //                       props.items.filter(eachExpense => eachExpense.date.year === filteredYear)

    if (filteredExpenses.length === 0) {
        <p>No expense found.</p>
    } else {
        filteredExpenses.map((expense) => (
            <ExpenseItem
                key={expense.id}
                title={expense.title}
                amount={expense.amount}
                date={expense.date}
            />
        ))
    }

    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter
                    selected={filteredYear}
                    onChangeFilter={filterChangeHandler}
                />
                {filteredExpenses.length === 0 ? <p>No expense found.</p> : filteredExpenses.map((expense) => (
                    <ExpenseItem
                        key={expense.id}
                        title={expense.title}
                        amount={expense.amount}
                        date={expense.date}
                    />
                ))}
            </Card>
        </div>

    );
}

export default Expenses;
