import "./Expenses.css"
import { useState } from "react";

// parent of
import Card from "../UI/Card";
import ExpensesFilter from "./ExpenseFilter";
import ExpensesList from "./ExpensesList";

function Expenses(props) {   // child of App, props is to receive items aka expenses from App
    const [filteredYear, setFilteredYear] = useState("2021");

    const filterChangeHandler = (selectedYear) => {
        console.log("Expenses.js filtered " + selectedYear);
        setFilteredYear(selectedYear);
    };

    const filteredExpenses = props.items.filter(eachExpense => (eachExpense.date.getFullYear().toString() === filteredYear)
    );
    // what i thought was    props.items.filter(eachExpense => eachExpense.date.year === filteredYear)

    let filter = <button onClick={clickHandler}>Add New Expense</button>;
    const [showFilter, setShowFilter] = useState();

    const clickHandler = () => {
        setShowFilter(true);
    };

    if (showFilter === true) {
        filter = <ExpensesFilter
            selected={filteredYear}
            onChangeFilter={filterChangeHandler}
        />;
    } else if (showFilter === false) {
        filter = <button onClick={clickHandler}>Add New Expense</button>;
    }

    return (
        <li>
            <Card className="expenses">
                {filter}
                <ExpensesList
                    items={filteredExpenses}
                />

            </Card>
        </li>

    );
}

export default Expenses;
