import React, { useState } from 'react';
import './NewUserForm.module.css'

const NewUserForm = props => {
    // record states
    const [enteredName, setEnteredName] = useState('');
    const [enteredAge, setEnteredAge] = useState('');
    const [nameInvalid, setNameInvalid] = useState(false);
    const [ageInvalid, setAgeInvalid] = useState(false);

    // update states dynamically
    const enteredNameHandler = event => {
        setEnteredName(event.target.value);
    };

    const enteredAgeHandler = event => {
        setEnteredAge(event.target.value);
    };


    // evaluate validity: empty name/age and negative age not allowed
    if (enteredName.trim().length === 0) {
        setNameInvalid(true);
    }
    else {
        setNameInvalid(false);
    }

    if ((enteredAge.trim().length === 0) | (enteredAge.trim() < 0)) {
        setAgeInvalid(true);
    }
    else {
        setAgeInvalid(false);
    }

    // todo when submitted
    const submitHandler = event => {
        event.preventDefault();  // to avoid leaving the page after submitting

        // merge name and age into one object
        const enteredUserData = {
            name: enteredName,
            age: enteredAge
        };

        if (nameInvalid === true) {
            console.log('Invalid username')
            return nameInvalid;
        }
        else {
            return enteredUserData;
        }

        if (ageInvalid === true) {
            console.log('Invalid age')
        }
        else {
            // return data
        }

        props.saveUserData(enteredUserData);

        setEnteredName('');
        setEnteredAge('');

    };


    return <form onSubmit={submitHandler}>
        <div>
            <h3>Username</h3>
            <input type='text' onChange={enteredNameHandler} value={enteredName} />
        </div>
        <div>
            <h3>Age (Years)</h3>
            <input type='number' min='0' step='1' onChange={enteredAgeHandler} value={enteredAge} />
        </div>
        <div>
            <button type='submit' value={enteredUserData}>Add User</button>
        </div>
    </form>;
};

export default NewUserForm;
