import AlertWindow from './components/AlertWindow/AlertWindow';

const DUMMY_USER_DATA = [
    {
        id: 'u1',
        name: 'Max',
        age: 30
    },
    {
        id: 'u2',
        name: 'Anne',
        age: 20
    }
];

