        console.log('validity check in App.js');
        console.log('name is valid? ' + nameInvalid);
        console.log('age is valid? ' + ageInvalid);

        // // invalid name & age
        // if ((nameNotValid === false) || (ageNotValid === false)) {
        //     // return <AlertWindow nameIsValid={nameIsValid} ageIsValid={props.ageIsValid} />;
        //     alert('invalid input occured');
        // } else {
        //     setUserList(prevData => { return [userList, ...prevData]; })
        // }
        setUserList(prevData => { return [userList, ...prevData]; })
    };

    // valid input
