import React, { useState } from 'react';
import './App.module.css';

import AddUser from './components/NewUser/AddUser';
import UserList from './components/UserList/UserList';
import AlertWindow from './components/AlertWindow/AlertWindow';

const DUMMY_USER_DATA = [
    {
        id: 'u1',
        name: 'Max',
        age: 30
    },
    {
        id: 'u2',
        name: 'Anne',
        age: 20
    }
];

const App = props => {
    let content = 'valid input';
    const [userList, setUserList] = useState(DUMMY_USER_DATA);



    const nameInvalidHandler = nameInvalid => {
        console.log('               ' + nameInvalid);
        content = 'name invalid';
        alert('name invalid');
        // let nameNotValid = nameInvalid;

    };

    const ageInvalidHandler = ageInvalid => {
        console.log(props);
        content = 'age invalid';
        alert('age invalid');
        // let ageNotValid = ageInvalid;

    };

    console.log(content);





    const saveDataHandler = (nameNotValid, ageNotValid, userList) => {
        // updatedData = userList.unshift(props.userData);
        // console.log('Data updated!')
        // return updatedData;

        console.log('validity check in App.js');
        console.log('name is valid? ' + nameNotValid);
        console.log('age is valid? ' + ageNotValid);

        // invalid name & age
        if ((nameNotValid === false) || (ageNotValid === false)) {
            // return <AlertWindow nameIsValid={nameIsValid} ageIsValid={props.ageIsValid} />;
            alert('invalid input occured');
        } else {
            setUserList(prevData => { return [userList, ...prevData]; })
        }
    };

    // valid input
    return <div className='body'>
        <AddUser saveUserData={saveDataHandler} onNameInvalid={nameInvalidHandler} onAgeInvalid={ageInvalidHandler} />
        {/* <AddUser saveUserData={saveDataHandler} /> */}
        <UserList userData={userList} />
    </div>

};

export default App;
