import React, { useState } from 'react';
import './NewUserForm.module.css'

const NewUserForm = (props) => {
    const [enteredName, setEnteredName] = useState('');
    const [enteredAge, setEnteredAge] = useState('');
    const [nameInvalid, setNameInvalid] = useState(false);
    const [ageInvalid, setAgeInvalid] = useState(false);

    if (enteredName.trim().length === 0) {
        setNameInvalid(true);
    }
    else {
        setNameInvalid(false);
    }

    if ((enteredAge.trim().length === 0) | (enteredAge.trim() < 0)) {
        setAgeInvalid(true);
    }
    else {
        setAgeInvalid(false);
    }

    const submitHandler = event => {
        event.preventDefault();
        if (nameInvalid === true) {
            console.log('Invalid username')
            return;
        }
        else {
            // return data
        }

        if (ageInvalid === true) {
            console.log('Invalid age')
        }
        else {
            // return data
        }
        const enteredData = {
            name: enteredName,
            age: enteredAge
        };

        setEnteredName('');
        setEnteredAge('');

    };

    const enteredNameHandler = event => {
        setEnteredName(event.target.value);
    };

    const enteredAgeHandler = event => {
        setEnteredAge(event.target.value);
    };


    return <div>
        <h3>Username</h3>
        <input type='text' onChange={enteredNameHandler} />

        <h3>Age (Years)</h3>
        <input type='number' min='0' step='1' onChange={enteredAgeHandler} />

        <button type='submit' onClick={submitHandler} value={enteredData}>Add User</button>
    </div>;
};

export default NewUserForm;
