import React, { useState } from 'react';
import './AddUser.module.css'

const AddUser = props => {

    // record states
    const [enteredName, setEnteredName] = useState('');
    const [enteredAge, setEnteredAge] = useState('');
    const [nameIsValid, setNameIsValid] = useState(true);
    const [ageIsValid, setAgeIsValid] = useState(true);

    // update states dynamically
    const nameChangeHandler = event => {
        setEnteredName(prevState => { return event.target.value; })
        // setNameIsValid(prevState => { return true; });
    };

    const ageChangeHandler = event => {
        setEnteredAge(prevState => { return event.target.value; });
        // setAgeIsValid(prevState => { return true; });
    };

    // todo when submitted
    const submitHandler = event => {
        event.preventDefault();  // to avoid leaving the page after submitting

        // merge name and age into one object, add id
        const enteredUserData = {
            id: Math.random().toString(),
            name: enteredName,
            age: +enteredAge,
        };
        console.log(enteredUserData);

        // evaluate validity: empty name/age and negative age not allowed
        if (props.enteredUserData.name.trim().length === 0) {
            setNameIsValid(prevState => { return false; });
        }

        if ((props.enteredUserData.age.trim().length === 0) | (props.enteredUserData.age.trim() < 0)) {
            setAgeIsValid(prevState => { return false; });
        }

        props.saveUserData(enteredUserData);
        console.log('New user added!');

        setEnteredName('');
        setEnteredAge('');
    }

    return <form onSubmit={submitHandler} className='body'>
        <div>
            <h3>Username</h3>
            <input className='input' type='text' onChange={nameChangeHandler} value={enteredName} onNameIsValid={nameIsValid} />
            {/* <input className='input' type='text' onChange={nameChangeHandler} value={enteredName} /> */}
        </div>
        <div>
            <h3>Age (Years)</h3>
            <input type='number' min='0' step='1' onChange={ageChangeHandler} value={enteredAge} onAgeIsValid={ageIsValid} />
            {/* <input type='number' min='0' step='1' onChange={ageChangeHandler} value={enteredAge} /> */}
        </div>
        <div>
            <button type='submit' >Add User</button>
        </div>
    </form>;
};

export default AddUser;
