import React, { useState, useEffect } from 'react';
import classes from './Cart.module.css';
import Modal from '../../UI/Modal';
import CartItem from '../Cart/CartItem';

const Cart = (props) => {
  // const [cartItems, setCartItems] = useState({
  //   id: 'c1',
  //   name: 'Sushi',
  //   amount: 2,
  //   price: 12.99,
  // });
  // useEffect(
  //   () => {
  //     // setCartItems([...cartItems, props.addedMeal])
  //   },
  //   [cartItems, props.addedMeal]
  // );
  const cartItems = [
    {
      id: 'c1',
      name: 'Sushi',
      amount: 2,
      price: 12.99,
    },
  ].map(item => <CartItem item={item} classes={classes['cart-items']} />);

  return (
    <Modal >
      <p>{props.addedMeal}</p>
      <p>{props.addedAmount}</p>
      {/* <CartItem classes={classes['cart-items']} /> */}
      {cartItems}
      <p className={classes.total}>Total Amount ${props.total}</p>
      <div className={classes.actions}>
        <button>Close</button>
        <button>Order</button>
      </div>
    </Modal>
  );
};

export default Cart;
