import React, { useState, useReducer, useContext } from 'react';
import Header from './components/Header/Header';
import AvailableMeals from './components/Meals/AvailableMeals';
import MealsSummary from './components/MealsSummary/MealsSummary';
import Cart from './components/Cart/Cart';
import CartItemsContext from './CartItemsContext';
import CartItemDispatch from './CartItemsContext';

const App = () => {
  const [cartModal, setCartModal] = useState(null);
  const [mealToAdd, setMealToAdd] = useState(null);
  const [amountToAdd, setAmountToAdd] = useState(null);

  const addHandler = (meal, amount) => {
    setMealToAdd(() => meal);
    setAmountToAdd(() => amount);
  };

  return (
    <>
      {cartModal}
      <Header
        onShowCartModal={
          (show) => {
            show && setCartModal(
              <Cart
                addedMeal={mealToAdd}
                addedAmount={amountToAdd}
                onClose={(close) => { !close && setCartModal(() => null) }}
              />
            )
          }
        } />
      <MealsSummary />
      <AvailableMeals onAdd={addHandler} />
    </>
  );
};

export default App;
