import React, { useState, useEffect } from 'react';
import classes from './Cart.module.css';
import Modal from '../../UI/Modal';
import CartItem from '../Cart/CartItem';

const Cart = ({ addedMeal, addedAmount, onClose }) => {
  // const [cartItems, setCartItems] = useState({
  //   id: 'c1',
  //   name: 'Sushi',
  //   amount: 2,
  //   price: 12.99,
  // });
  // useEffect(
  //   () => {
  //     // setCartItems([...cartItems, props.addedMeal])
  //   },
  //   [cartItems, props.addedMeal]
  // );

  const cartItems = (<ul classes={classes['cart-items']}>
    {[addedMeal,].map(item => <CartItem
      name={item.name}
      price={item.price}
      amount={addedAmount}
    >{item.name}</CartItem>)}
  </ul>);

  const closeHandler = () => {
    console.log('closing');
    onClose(true);
  };

  const orderHandler = (e) => {
    e.preventDefault();
    console.log('ordering');
  };

  return (
    <Modal >
      {/* <CartItem classes={classes['cart-items']} /> */}
      {cartItems}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>$50</span>
      </div>
      <div className={classes.actions}>
        <button onClick={closeHandler} className={classes['button--alt']}>Close</button>
        <button onClick={orderHandler} className={classes.button}>Order</button>
      </div>
    </Modal>
  );
};

export default Cart;
