import { createSlice } from "@reduxjs/toolkit";

const CartSlice = createSlice({
    name: 'cart',
    initialState: [{
        item: '',
        amount: null,

    }],
    reducers: {
        toggle(state) {
            state.cartIsVisible = !state.cartIsVisible;
        }
    },
});

export default UISlice;
export const UISliceActions = UISlice.actions;
