import Card from '../UI/Card';
import classes from './Cart.module.css';
import CartItem from './CartItem';
import { useSelector, useDispatch } from 'react-redux';
import { CartSliceActions } from '../../store/cart-slice';
import { useCallback } from 'react';

const Cart = () => {
  const items = useSelector(state => state.cart.items);
  const dispatch = useDispatch();

  const fetchItemsHandler = useCallback(async () => {
    const response = await fetch('https://react-http-5a556-default-rtdb.asia-southeast1.firebasedatabase.app/');
    const data = await response.json();
    dispatch(CartSliceActions.addItemToCart({
      name: data.name,
      title: data.title,
      price: data.price,
      totalPrice: data.totalPrice,
      quantity: data.quantity,
    }));
    // const loadedItems = [];
    // loadedItems.push({
    //   name: data.name,
    //   title: data.title,
    //   price: data.price,
    //   totalPrice: data.totalPrice,
    //   quantity: data.quantity,
    // });
  }, [])

  async function addItemsHandler(cartItems) {
    const response = await fetch('https://react-http-5a556-default-rtdb.asia-southeast1.firebasedatabase.app/', {
      method: 'POST',
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(cartItems),
    });
    const data = await response.json();
    console.log(data)
  };

  return (
    <Card className={classes.cart}>
      <h2>Your Shopping Cart</h2>
      <ul>
        {items.map(eachItem =>
          <CartItem
            name={eachItem.name}
            id={eachItem.id}
            price={eachItem.price}
            quantity={eachItem.quantity}
            totalPrice={eachItem.totalPrice}
          />)}
      </ul>
    </Card>
  );
};

export default Cart;
