import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { useSelector, useDispatch } from 'react-redux';
import { UISliceActions } from './store/ui-slice';
import { useEffect } from 'react';
import Notification from './components/UI/Notification';

let isInitial = true;

function App() {
  const dispatch = useDispatch();
  const showCart = useSelector(state => state.ui.cartIsVisible);
  const cart = useSelector(state => state.cart);
  const notification = useSelector(state => state.ui.notification);

  // const fetchItemsHandler = useEffect(async () => {
  //   const response = fetch('https://react-http-5a556-default-rtdb.asia-southeast1.firebasedatabase.app/');
  //   const data = response.json();
  //   dispatch(CartSliceActions.addItemToCart({
  //     name: data.name,
  //     title: data.title,
  //     price: data.price,
  //     totalPrice: data.totalPrice,
  //     quantity: data.quantity,
  //   }));
  //   // const loadedItems = [];
  //   // loadedItems.push({
  //   //   name: data.name,
  //   //   title: data.title,
  //   //   price: data.price,
  //   //   totalPrice: data.totalPrice,
  //   //   quantity: data.quantity,
  //   // });
  // }, [])

  useEffect(() => {
    async function sendCartData() {
      dispatch(
        UISliceActions.showNotification({
          status: 'pending',
          title: 'Sending...',
          message: 'Sending cart data!',
        }));
      const response = await fetch('https://react-http-5a556-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json', {
        method: 'PUT',
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(cart),
      });
      if (!response.ok) {
        throw new Error('Sending cart data failed');
      }
      dispatch(
        UISliceActions.showNotification({
          status: 'success',
          title: 'Success!',
          message: 'Sent cart data successfully!',
        })
      );
    }

    if (isInitial) {
      isInitial = false;
      return;
    }

    sendCartData().catch(error => {
      dispatch(
        UISliceActions.showNotification({
          status: 'error',
          title: 'Failed!',
          message: 'Sending cart data failed',
        })
      );
    });

  }, [cart, dispatch]);

  return (
    <>
      {notification && <Notification
        status={notification.status}
        title={notification.title}
        message={notification.message}
      />}
      <Layout>
        {showCart && <Cart />}
        <Products />
      </Layout>
    </>
  );
}

export default App;
